INSERT INTO `tbl_kutipan` VALUES (3, 'Islami', 'Allah tidak akan pernah membebani seseorang melainkan sesuai dengan kesanggupannya,', 'Q.S. Al-Baqarah (2:286)', 1, '2023-05-08 09:37:53', '2023-05-08 09:37:53');
INSERT INTO `tbl_kutipan` VALUES (4, 'Islami', '“Dan janganlah kamu merasa lemah, dan jangan pula (kamu merasa) bersedih hati. Sebab, kamu paling tinggi derajatnya jika kamu (termasuk) orang yang beriman', 'Q.S. Al-Imran (3:139)', 1, '2023-05-08 09:38:05', '2023-05-08 09:38:33');
INSERT INTO `tbl_kutipan` VALUES (5, 'Islami', 'Dan Dia Allah akan memberi manusia rezeki dari arah yang tak disangka-sangka. Dan barangsiapa yang bertawakal kepada Allah, niscaya Allah ‘kan cukupkan (keperluan)nya. Sungguh, Allah melaksanakan urusan-Nya (menepati janji-Nya),', 'Q.S At-Talaq (65:3).', 1, '2023-05-08 09:38:06', '2023-05-08 09:38:40');
INSERT INTO `tbl_kutipan` VALUES (6, 'Islami', 'Wahai orang-orang yang beriman! Mohonlah pertolongan kepada Allah dengan sabar dan shalat. Sungguh, Allah (akan selalu) bersama orang-orang yang sabar,', 'Q.S Al-Baqarah (2:153).', 1, '2023-05-08 09:38:06', '2023-05-08 09:38:51');
INSERT INTO `tbl_kutipan` VALUES (7, 'Islami', 'Juallah kehidupanmu saat ini untuk kehidupanmu selanjutnya, akhirat, niscaya kamu akan mendapatkan keduanya. Tetapi, jika kamu menjual kehidupan selanjutnya untuk kehidupan saat ini, maka kamu akan kehilangan keduanya,', 'Hasan Al-Basri', 1, '2023-05-08 09:38:07', '2023-05-08 09:39:01');
INSERT INTO `tbl_kutipan` VALUES (8, 'Islami', '“Dunia ini ibarat bayangan. Jika kamu berusaha menangkapnya, dia akan lari. Tetapi, jika kamu membelakanginya, maka dia tak punya pilihan selain untuk mengikutimu,', 'Ibnu Qayyim.\r\nIbnu Qayyim.\r\nIbnu Qayyim.', 1, '2023-05-08 09:38:08', '2023-05-08 09:39:18');
INSERT INTO `tbl_kutipan` VALUES (9, 'Islami', 'Keikhlasan adalah rahasia antara Allah dengan hamba-Nya. Bahkan, malaikat pencatat pun tidak dapat mengetahui hal itu barang sedikit guna dituliskannya dalam catatan amal. Setan juga tidak mengetahui sehingga dia tidak bisa merusaknya, nafsu pun tidak menyadari sehingga ia tidak bisa mempengaruhinya', 'Junayd Al-Baghdadi', 1, '2023-05-08 09:38:08', '2023-05-08 09:39:27');
INSERT INTO `tbl_kutipan` VALUES (10, 'Islami', 'Terkadang Allah akan membiarkanmu merasakan pahitnya kehidupan dunia, sehingga kamu dapat menghargai dan mencicipi manisnya iman,', 'Omar Suleiman.', 1, '2023-05-08 09:38:09', '2023-05-08 09:39:38');
INSERT INTO `tbl_kutipan` VALUES (11, 'Islami', 'Nasibmu telah ditulis oleh tinta cinta-Nya. Kemudian disegel dengan rahmat-Nya. Jadi tak perlu takut, percayakanlah dirimu kepada-Nya dan berharaplah pada segala ketetapan-Nya', 'Gems of Jannah', 1, '2023-05-08 09:38:09', '2023-05-08 09:39:48');
INSERT INTO `tbl_kutipan` VALUES (12, 'Islami', 'Allah selalu menjawab doamu dengan tiga cara. Pertama, langsung mengabulkannya. Kedua, menundanya. Ketiga, menggantinya dengan yang lebih baik untukmu', 'Anonim', 1, '2023-05-08 09:38:10', '2023-05-08 09:39:58');
INSERT INTO `tbl_kutipan` VALUES (13, 'Islami', 'Jika apa yang kamu harapkan tidak terjadi, maka kamu harus mencoba untuk menyukai apa-apa yang sedang terjadi', 'Anonim', 1, '2023-05-08 09:38:10', '2023-05-08 09:40:06');
INSERT INTO `tbl_kutipan` VALUES (14, 'Islami', 'Takutlah pada Allah karena hukumannya. Cintai Dia, karena Allah sangat penuh dengan belas kasihan,', 'Anonim', 1, '2023-05-08 09:38:11', '2023-05-08 09:40:14');
INSERT INTO `tbl_kutipan` VALUES (15, 'Islami', 'Allah menjadikanmu seorang Muslim karena ingin melihatmu berada di surga-Nya. Dan yang perlu kamu lakukan adalah menunjukkan jika kamu pantas untuk itu', 'Anonim', 1, '2023-05-08 09:38:11', '2023-05-08 09:40:23');
INSERT INTO `tbl_kutipan` VALUES (16, 'Islami', 'Setiap kamu akan tidur, cobalah untuk mulai merenung. Apakah langkahmu hari ini sudah membuatmu semakin dekat dengan Allah? Atau malah sebaliknya, membuatmu semakin jauh dari Allah?”', 'Anonim', 1, '2023-05-08 09:38:12', '2023-05-08 09:40:35');
INSERT INTO `tbl_kutipan` VALUES (17, 'Islami', 'Ketika rasa sakit hatimu membuatmu jatuh, ingatlah Allah melihat semuanya dan sedang merencanakan hal baik lainnya', 'Anonim', 1, '2023-05-08 09:38:12', '2023-05-08 09:40:44');
INSERT INTO `tbl_kutipan` VALUES (18, 'Islami', 'Petunjuk tidak bisa dicapai kecuali oleh pengetahuan. Dan, arah tujuan yang benar tidak akan bisa dituju kecuali dengan kesabaran', 'Anonim', 1, '2023-05-08 09:38:13', '2023-05-08 09:40:49');
INSERT INTO `tbl_kutipan` VALUES (19, 'Islami', 'Teruslah berusaha dengan harapan apa yang kamu impikan. Dan janganlah lupa untuk mendampingi usaha tersebut dengan kesabaran serta keikhlasan', 'Anonim', 1, '2023-05-08 09:38:13', '2023-05-08 09:40:58');
INSERT INTO `tbl_kutipan` VALUES (20, 'Islami', 'Setelah kamu memohon bimbingan menuju jalan yang lurus kepada Allah, maka jangan hanya berdiri di tempat. Mulailah melangkah', 'Anonim', 1, '2023-05-08 09:38:15', '2023-05-08 09:40:59');
